const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function RowAndColumnSpacing() {
  const handleClick = (id) => {
    console.log(`Click en imagen ${id}`);
  };

  return (
    <Box sx={{ width: '50%' }}>
      <Grid container columnSpacing={{ xs: 1, sm: 2, md: 3 }} wrap="wrap">
        {proyectos.map((proyecto) => (
          <Grid item xs={4} key={proyecto.id}>
            <Item onClick={() => handleClick(proyecto.id)}>
              <img src={proyecto.ima} style={{ width: '33.33%', height: 'auto', objectFit: 'cover' }}></img>
            </Item>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
