const Item = styled('div')(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    '& > img': {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
    },
  }));
  
  export default function RowAndColumnSpacing() {
    const handleClick = (id) => {
      console.log(`Clicked on image with ID ${id}`);
    };
  
    return (
      <Box sx={{ width: '50%' }}>
        <Grid container columnSpacing={{ xs: 1, sm: 2, md: 3 }} wrap="wrap">
          {proyectos.map((proyecto) => (
            <Grid item xs={4} key={proyecto.id}>
              <Item>
                <button onClick={() => handleClick(proyecto.id)}>
                  <img src={proyecto.ima} alt={proyecto.nombre} />
                </button>
              </Item>
            </Grid>
          ))}
        </Grid>
      </Box>
    );
  }
