import './App.css';
import './css/header.css';
import './css/body.css';
import './css/footer.css';
import './css/card-layout.css';
import Granportafolio from './components/granporfotilio'
import Portfoliothumbs from './components/porfotliothumbs'
import MenuP from './components/menuP'

import * as React from 'react';


function App() {

  const [selectedImage, setSelectedImage] = React.useState(null);

  const handleImageClick = (image) => {
    setSelectedImage(image);
  };

  return (
    <div className="App">
      <header>
        <MenuP/>
      </header>
      <main>
        <div style={{ display: 'flex', flexWrap: 'nowrap' }}>
          <Granportafolio selectedImage={selectedImage} />
          <Portfoliothumbs onImageClick={handleImageClick} />
          </div>
      </main>
      <footer>Footer</footer>
  </div>
  );
}

export default App;
