import * as React from 'react';
import { styled } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import imagen from'../static/images/cards/portfolio.jpg'



const proyectos = [
    {
      id: 1,
      nombre: 'Proyecto 1',
      descripcion: 'Descripción del proyecto 1',
      ima: imagen
    },
    {
      id: 2,
      nombre: 'Proyecto 2',
      descripcion: 'Descripción del proyecto 2',
      ima: imagen
    },
    {
      id: 3,
      nombre: 'Proyecto 3',
      descripcion: 'Descripción del proyecto 3',
      ima: imagen
    },
    {
      id: 4,
      nombre: 'Proyecto 4',
      descripcion: 'Descripción del proyecto 4',
      ima: imagen
    },
    {
      id: 5,
      nombre: 'Proyecto 5',
      descripcion: 'Descripción del proyecto 5',
      ima: imagen
    },
    {
      id: 6,
      nombre: 'Proyecto 6',
      descripcion: 'Descripción del proyecto 6',
      ima: imagen
    }
  ];
  

// const Item = styled(Paper)(({ theme }) => ({
//   backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
//   ...theme.typography.body2,
//   padding: theme.spacing(1),
//   textAlign: 'center',
//   color: theme.palette.text.secondary,
// }));

const Item = styled('div')(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    '& > img': {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
    },
  }));

export default function RowAndColumnSpacing() {
    const handleClick = (nombreProyecto) => {
        console.log(`Se hizo clic en el botón de imprimir del proyecto ${nombreProyecto}`);
      };

  return (
    <Box sx={{ width: '50%' }}>
      <Grid container columnSpacing={{ xs: 1, sm: 2, md: 3 }} >
          {proyectos.map((proyecto) => (
            <Grid item xs={4} key={proyecto.id}>
                <Item>
                    <button onClick={() => handleClick(proyecto.id)}>
                        <img src={proyecto.ima} alt={proyecto.nombre} />
                    </button>
                </Item>
            </Grid>
          ))}
            
      </Grid>
    </Box>
  );
}
