import * as React from 'react';
import { styled } from '@mui/material/styles';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import imagen from'../static/images/cards/portfolio.jpg'

const proyectos = [
  {
    id: 1,
    nombre: 'Proyecto 1',
    descripcion: 'Descripción del proyecto 1',
    ima: imagen
  },
  {
    id: 2,
    nombre: 'Proyecto 2',
    descripcion: 'Descripción del proyecto 2',
    ima: imagen
  },
  {
    id: 3,
    nombre: 'Proyecto 3',
    descripcion: 'Descripción del proyecto 3',
    ima: imagen
  },
  {
    id: 4,
    nombre: 'Proyecto 4',
    descripcion: 'Descripción del proyecto 4',
    ima: imagen
  },
  {
    id: 5,
    nombre: 'Proyecto 5',
    descripcion: 'Descripción del proyecto 5',
    ima: imagen
  },
  {
    id: 6,
    nombre: 'Proyecto 6',
    descripcion: 'Descripción del proyecto 6',
    ima: imagen
  }
];

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

export default function RowAndColumnSpacing() {
  const handleClick = (id) => {
    console.log(`Click en imagen ${id}`);
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Box sx={{ width: '40%' }}>
        <Box sx={{ mb: 2 }}>
          <Item>Titulo de la imagen seleccionada</Item>
        </Box>
        <Grid container columnSpacing={{ xs: 1, sm: 2, md: 3 }} wrap="wrap">
          {proyectos.map((proyecto) => (
            <Grid item xs={4} key={proyecto.id}>
              <Item onClick={() => handleClick(proyecto.id)}>
                <img src={proyecto.ima} style={{ width: '100%', height: 'auto', objectFit: 'cover' }} alt={proyecto.nombre} />
              </Item>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Box>
  );
}
